import React, { Component } from 'react'
import  {Consumer} from '../Context';

export class Details extends Component {

    
    render() {
        return (
            <Consumer>
            {(value)=>{
                if(value.selectedMovie ==null)
                {
                    return (
                        <h3 className="jumbotron">You did not select any Movie</h3>
                    )
                }
                else{

                
                const {popularity,vote_count,poster_path,backdrop_path,
                    original_title,title,vote_average,overview,release_date
                } = value.selectedMovie;
                return (
            <React.Fragment>
                  <section className="section">
        <div className="container"  
       
        >

          <div className="row">

            <div className="col-md-6 ml-auto order-md-last mb-7 mb-md-0">
              <a className="product-media" href="details.html">
                <button className=" btn-circle float-right "> <span>{vote_average}</span> </button>
                <img src={"https://image.tmdb.org/t/p/original/"+poster_path} alt="product"/>
              </a>
              </div>

            <div className="col-11 mx-auto col-md-5 mx-md-0"  
            style={{  
            backgroundImage: "url(" + "https://image.tmdb.org/t/p/original/"+backdrop_path + ")",
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat',
            color:'white',
            fontSize:'20px'
          }}>
              <h2>{title}</h2>
                <p className="text-light my-6">{overview}</p>

              <ul className="list-unstyled">
                <li><span className="mr-1 ti-check text-success small-3"></span> Built in GPS</li>
                <li><span className="mr-1 ti-check text-success small-3"></span> Heart Rate Sensor</li>
                <li><span className="mr-1 ti-check text-success small-3"></span> Water Resistant 50 Meters</li>
                <li><span className="mr-1 ti-check text-success small-3"></span> Comprehensive Workout App</li>
              </ul>

              <div className="row gap-y align-items-center text-center bg-light rounded p-5 mt-7">
                <div className="col-md-auto ml-auto order-md-last">
                  <h4 className="lead-5 mb-0 lh-1 fw-500">$399</h4>
                  <small className="text-lighter">+ $10 shipping fees</small>
                </div>

                <div className="col-md-auto">
                  <a className="btn btn-lg btn-primary" href="#">Purchase</a>
                </div>
              </div>
            </div>

          </div>

        </div>
      </section>

            </React.Fragment>
                 )
                }
            }}
            </Consumer>
        )
    }
}

export default Details
